mapper.update:
update stable
set idname = #{idname,jdbcType=VARCHAR},
  idnameTeamNum = #{idnameteamnum,jdbcType=INTEGER},
  nickname = #{nickname,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
